package datatypes;

public class PrimitiveTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//These are all the primitive data types in java
		byte b = 10;
		short s = 20;
		int i = 30;
		long l = 40l;
		float f = 5.5f;
		double d = 6.66;
		char c = 'A';
		boolean bool = false;
		
		System.out.println("These are all the primitive or basic data types in java");
	}

}
