package oops_concepts;

public class FinalKeyword {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Final Keyword");
		System.out.println("Variable, Method, and Class can be Final");
		System.out.println("Final varibale value cannot be changed. A blank final variable can only be initialized inside constructor");
		System.out.println("final can also be used with static vars.");
		System.out.println("A method also can be final. Once declared final it cannot be overridden");
		System.out.println("A Class can also be final. Once declared as final it cannot be inherited");
		
		
	}

}
